# Fonction pour envoyer 5 tweets aléatoires
def say_hello_to_journalists(client)
  selected = JOURNALISTS.sample(5)
  selected.each do |handle|
    message = "#{handle} Bonjour à vous ! Continuez votre super travail #hello_world"
    #client.update(message)
    puts "Simulation : #{message}"
    puts "Tweet envoyé à #{handle}"
    sleep(2) # petit délai pour éviter un blocage API
  end
end

