def like_hello_world_tweets
  # Simulation de 5 tweets récents contenant #hello_world
  fake_tweets = [
    { user: "TechGuru", text: "J'adore Ruby ! #hello_world" },
    { user: "CodeLover", text: "Premier bot terminé #hello_world" },
    { user: "WebDevSN", text: "Ruby + Twitter = #hello_world" },
    { user: "AminaDev", text: "Hello world en Ruby, done ! #hello_world" },
    { user: "DjimoG", text: "Apprentissage réussi #hello_world" }
  ]

  fake_tweets.each do |tweet|
    puts "Simulation : Liking tweet de @#{tweet[:user]} - #{tweet[:text]}"
    puts "Tweet liké (simulation)"
  end
end


# Fonction pour follow 20 personnes ayant tweeté #hello_world
def follow_hello_world_users(client)
  tweets = client.search("#hello_world", result_type: "recent").take(20)
  tweets.each do |tweet|
    client.follow(tweet.user)
    puts "Suivi de @#{tweet.user.screen_name}"
  end
end

# Fonction principale
def perform
  client = login_twitter
  say_hello_to_journalists(client)
  like_hello_world_tweets(client)
  follow_hello_world_users(client)
end

# Exécute le programme uniquement si le fichier est lancé directement
perform if __FILE__ == $0

